import React, { useState } from 'react';
import SentimentResults from './components/SentimentResults';

function App() {
  // State to control the visibility of SentimentResults
  const [showSentiment, setShowSentiment] = useState(true);

  // Function to toggle the visibility of SentimentResults
  const toggleSentiment = () => {
    setShowSentiment(!showSentiment);
  };

  return (
    <div className="App">
      {/* Header Bar */}
      <header className="App-header">
        <h1 className="App-title">Sentiment Analysis Webpage</h1>
        <div className="header-actions">
          <button className="refresh-button" onClick={() => window.location.reload()}>Refresh</button>
          <button className="toggle-button" onClick={toggleSentiment}>
            {showSentiment ? 'Hide Results' : 'Show Results'}
          </button>
        </div>
      </header>

      {/* Content section for sentiment results */}
      <div className="content">
        {showSentiment && (
          <>
            <h2>Sentiment Analysis Results:</h2>
            <SentimentResults />
          </>
        )}
        {!showSentiment && (
          <p>The sentiment results are hidden. Click "Show Results" to view.</p>
        )}
      </div>
    </div>
  );
}

export default App;
