import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import "./App.css";
import TweetsPage from "./pages/TweetsPage";
import SentimentPage from "./pages/SentimentPage";

function App() {
    const [tweets, setTweets] = useState([]); // Store tweets globally

    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <Link to="/tweets" className="nav-link">Tweets</Link>
                    <Link to="/sentiment" className="nav-link">Sentiment Analysis</Link>
                </header>

                <Routes>
                    <Route path="/tweets" element={<TweetsPage setTweets={setTweets} tweets={tweets} />} />
                    <Route path="/sentiment" element={<SentimentPage tweets={tweets} />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
