import React from "react";

const SentimentPage = ({ tweets }) => {
    return (
        <div className="sentiment-page">
            <h2>Sentiment Analysis</h2>
            {tweets.length > 0 ? (
                <ul className="tweet-list">
                    {tweets.map((tweet) => (
                        <li key={tweet.id} className="tweet-item">
                            <strong>{tweet.name} (@{tweet.username})</strong>: {tweet.text} ‚ù§Ô∏è {tweet.likes} üîÑ {tweet.retweets}
                            <span className={`sentiment ${tweet.sentiment.toLowerCase()}`}>[{tweet.sentiment}]</span>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No tweets found. Fetch tweets first on the <a href="/tweets">Tweets Page</a>.</p>
            )}
        </div>
    );
};

export default SentimentPage;
