import React, { useState } from "react";
import axios from "axios";

const SentimentResults = () => {
    const [tweets, setTweets] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchTweets = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await axios.get("http://localhost:5001/tweets");
            setTweets(response.data.data);
        } catch (error) {
            console.error("Error fetching tweets:", error);
            setError("Rate limit exceeded. Please wait a few minutes.");
        }
        setLoading(false);
    };

    return (
        <div className="sentiment-results">
            <h2>Recent Tweets on Cloud Computing</h2>
            <button onClick={fetchTweets} className="refresh-button">
                {loading ? "Loading..." : "Fetch Tweets"}
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <ul>
                {tweets.length > 0 ? (
                    tweets.map((tweet) => (
                        <li key={tweet.id} style={{ borderBottom: "1px solid #ddd", padding: "10px" }}>
                            {tweet.formatted_tweet} <br />
                            ‚ù§Ô∏è {tweet.likes} | üîÑ {tweet.retweets}
                        </li>
                    ))
                ) : (
                    <p>No tweets found.</p>
                )}
            </ul>
        </div>
    );
};

export default SentimentResults;
