import React, { useState } from "react";
import axios from "axios";

const SentimentResults = () => {
    const [tweets, setTweets] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchTweets = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get("http://localhost:5001/tweets");
            console.log("üì° API Response:", response.data); // Debugging log

            if (response.data && response.data.data && response.data.data.length > 0) {
                setTweets(response.data.data);
            } else {
                throw new Error("No tweets returned from backend.");
            }
        } catch (error) {
            console.error("‚ùå Error fetching tweets:", error);
            setError("Failed to load tweets. Please try again later.");
        }

        setLoading(false);
    };

    return (
        <div className="sentiment-results">
            <h2>Recent Tweets on Cloud Computing</h2>
            <button onClick={fetchTweets} className="refresh-button" disabled={loading}>
                {loading ? "Loading..." : "Fetch Tweets"}
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}

            <div className="tweet-list">
                {tweets.length > 0 ? (
                    tweets.map((tweet) => (
                        <p key={tweet.id} className="tweet-item">
                            <strong>{tweet.name} (@{tweet.username})</strong>: {tweet.text} ‚ù§Ô∏è {tweet.likes} üîÑ {tweet.retweets}
                        </p>
                    ))
                ) : (
                    !loading && <p>No tweets found. Click "Fetch Tweets" to refresh.</p>
                )}
            </div>
        </div>
    );
};

export default SentimentResults;
