// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import './App.css';
import SentimentResults from './components/SentimentResults';

function App() {
  const [showResults, setShowResults] = useState(true);

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Link to="/" className="App-title">Social Media Sentiment Analysis</Link>
          <div className="header-actions">
            <button onClick={() => window.location.reload()} className="refresh-button">
              Refresh
            </button>
            <button onClick={() => setShowResults(!showResults)} className="toggle-button">
              {showResults ? "Hide Results" : "Show Results"}
            </button>
          </div>
        </header>

        <Routes>
          <Route path="/" element={showResults ? <SentimentResults /> : <p>Results Hidden</p>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
