import React, { useState } from "react";
import axios from "axios";
import Tweets from "../components/Tweets"; // Import the tweet list component

const TweetsPage = ({ setTweets, tweets }) => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchTweets = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get("http://localhost:5001/tweets");
            console.log("üì° API Response:", response.data);

            if (response.data && response.data.data.length > 0) {
                setTweets(response.data.data); // Store tweets globally
            } else {
                throw new Error("No tweets found.");
            }
        } catch (error) {
            console.error("‚ùå Error fetching tweets:", error);
            setError("Failed to load tweets. Please try again later.");
        }

        setLoading(false);
    };

    return (
        <div className="tweets-page">
            <h2>Recent AI Tweets</h2>
            <button onClick={fetchTweets} className="refresh-button" disabled={loading}>
                {loading ? "Loading..." : "Fetch AI Tweets"}
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}

            {/* ‚úÖ Pass tweets to the Tweets component */}
            <Tweets tweets={tweets} />
        </div>
    );
};

export default TweetsPage;
