import React, { useState } from 'react';
import SentimentResults from './components/SentimentResults';

function App() {
  // State to control the visibility of SentimentResults
  const [showSentiment, setShowSentiment] = useState(true); // Set to false if you want to hide by default

  // Function to toggle the visibility of SentimentResults
  const toggleSentiment = () => {
    setShowSentiment(!showSentiment);
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* Custom Header Content */}
        <nav className="navbar">
          <h1 className="App-title">Social Media Sentiment Analysis</h1>
          <button className="refresh-button" onClick={() => window.location.reload()}>Refresh</button>
        </nav>
      </header>

      {/* Content Below the Navbar */}
      <div className="content">
        <h2>Sentiment Analysis Results:</h2>
        {/* Conditionally render SentimentResults component */}
        {showSentiment ? (
          <SentimentResults />
        ) : (
          <p>The sentiment results are hidden.</p>
        )}
      </div>

      {/* Button to toggle visibility */}
      <button onClick={toggleSentiment} className="toggle-button">
        {showSentiment ? 'Hide Results' : 'Show Results'}
      </button>
    </div>
  );
}

export default App;
