import React, { useState, useEffect } from "react";
import axios from "axios";

const Tweets = ({ setTweets }) => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchTweets();
    }, []); // ‚úÖ We'll fix the missing dependency warning below

    const fetchTweets = async () => {
        setLoading(true);
        setError(null);

        try {
            const response = await axios.get("http://localhost:5001/tweets");
            console.log("üì° API Response:", response.data);

            if (response.data && response.data.data.length > 0) {
                setTweets(response.data.data); // ‚úÖ Store tweets globally
            } else {
                throw new Error("No tweets found.");
            }
        } catch (error) {
            console.error("‚ùå Error fetching tweets:", error);
            setError("Failed to load tweets. Please try again later.");
        }

        setLoading(false);
    };

    return (
        <div className="tweets-container">
            <button onClick={fetchTweets} className="refresh-button" disabled={loading}>
                {loading ? "Loading..." : "Fetch AI Tweets"}
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Tweets;
